import type { Address } from 'viem'
import { describe, expect, test } from 'vitest'

import {
  accountA,
  accountB,
  MOCK_API_KEY,
  passkeyAccount,
} from '../../test/consts'
import { MODULE_TYPE_ID_VALIDATOR } from '../modules/common'
import {
  getAddress,
  getDeployArgs,
  getInstallData,
  getPackedSignature,
} from './nexus'

const MOCK_MODULE_ADDRESS = '0x28de6501fa86f2e6cd0b33c3aabdaeb4a1b93f3f'

describe('Accounts: Nexus', () => {
  describe('Deploy Args', () => {
    test('ECDSA owners', () => {
      const {
        factory,
        factoryData,
        salt,
        implementation,
        initializationCallData,
      } = getDeployArgs({
        owners: {
          type: 'ecdsa',
          accounts: [accountA, accountB],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(factory).toEqual('0x0000000000e70c5ebEBdEc2457508f6d1709bF55')
      expect(factoryData).toEqual(
        '',
      )
      expect(salt).toEqual(
        '0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470',
      )
      expect(implementation).toEqual(
        '0x00000000006a1bF4cBa18564Ecc916c3Cf768111',
      )
      expect(initializationCallData).toEqual(
        '',
      )
    })

    test('Passkey owner', () => {
      const {
        factory,
        factoryData,
        salt,
        implementation,
        initializationCallData,
      } = getDeployArgs({
        owners: {
          type: 'passkey',
          accounts: [passkeyAccount],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(factory).toEqual('0x0000000000e70c5ebEBdEc2457508f6d1709bF55')
      expect(factoryData).toEqual(
        '',
      )
      expect(salt).toEqual(
        '0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470',
      )
      expect(implementation).toEqual(
        '0x00000000006a1bF4cBa18564Ecc916c3Cf768111',
      )
      expect(initializationCallData).toEqual(
        '',
      )
    })
  })

  describe('Get Address', () => {
    test('ECDSA owners', () => {
      const address = getAddress({
        owners: {
          type: 'ecdsa',
          accounts: [accountA, accountB],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })
      expect(address).toEqual('0x0cd1c38d4cdffdbbd4ac192babf70110ad250668')
    })

    test('Passkey owner', () => {
      const address = getAddress({
        owners: {
          type: 'passkey',
          accounts: [passkeyAccount],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(address).toEqual('0x35e1c3f1ded30246aa2ca361342c3ee0d0749293')
    })
  })

  describe('Get Install Data', () => {
    test('Module', () => {
      const installData = getInstallData({
        address: MOCK_MODULE_ADDRESS,
        initData: '0xabcd',
        type: MODULE_TYPE_ID_VALIDATOR,
        deInitData: '0x0000',
        additionalContext: '0x0000',
      })

      expect(installData).toEqual(
        '',
      )
    })
  })

  describe('Get Packed Signature', () => {
    test('Mock signature', async () => {
      const hash = '0xabcd'
      const mockSignature = '0x1234'
      const validator = {
        address: '0xe35b75e5ec3c04e9cefa8e581fbee859f56edeb4' as Address,
        isRoot: true,
      }
      const signature = await getPackedSignature(
        async (_) => mockSignature,
        hash,
        validator,
      )

      expect(signature).toEqual(
        '0xe35b75e5ec3c04e9cefa8e581fbee859f56edeb41234',
      )
    })
  })
})
