import type { Address } from 'viem'
import { describe, expect, test } from 'vitest'

import {
  accountA,
  accountB,
  MOCK_API_KEY,
  passkeyAccount,
} from '../../test/consts'
import { MODULE_TYPE_ID_VALIDATOR } from '../modules/common'
import {
  getAddress,
  getDeployArgs,
  getInstallData,
  getPackedSignature,
} from './safe'

const MOCK_MODULE_ADDRESS = '0x28de6501fa86f2e6cd0b33c3aabdaeb4a1b93f3f'

describe('Accounts: Safe', () => {
  describe('Deploy Args', () => {
    test('ECDSA owners', () => {
      const {
        factory,
        factoryData,
        salt,
        implementation,
        initializationCallData,
      } = getDeployArgs({
        owners: {
          type: 'ecdsa',
          accounts: [accountA, accountB],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(factory).toEqual('0x4e1dcf7ad4e460cfd30791ccc4f9c8a4f820ec67')
      expect(factoryData).toEqual(
        '',
      )
      expect(salt).toEqual(
        '0xcd22c6be130453e1c7a5617c2792d41dd1d8a3ce4024819179507b20de8201f5',
      )
      expect(implementation).toEqual(
        '0x29fcb43b46531bca003ddc8fcb67ffe91900c762',
      )
      expect(initializationCallData).toEqual(null)
    })

    test('Passkey owner', () => {
      const {
        factory,
        factoryData,
        salt,
        implementation,
        initializationCallData,
      } = getDeployArgs({
        owners: {
          type: 'passkey',
          accounts: [passkeyAccount],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(factory).toEqual('0x4e1dcf7ad4e460cfd30791ccc4f9c8a4f820ec67')
      expect(factoryData).toEqual(
        '',
      )
      expect(salt).toEqual(
        '0x1774a1bf788cc08a2f87e82e78226fd1484c5dfc9d8ae3041cf96f478e470214',
      )
      expect(implementation).toEqual(
        '0x29fcb43b46531bca003ddc8fcb67ffe91900c762',
      )
      expect(initializationCallData).toEqual(null)
    })
  })

  describe('Get Address', () => {
    test('ECDSA owners', () => {
      const address = getAddress({
        owners: {
          type: 'ecdsa',
          accounts: [accountA, accountB],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(address).toEqual('0xd1f90bf932b56145800b608d9dd15c653270f042')
    })

    test('Passkey owner', () => {
      const address = getAddress({
        owners: {
          type: 'passkey',
          accounts: [passkeyAccount],
        },
        rhinestoneApiKey: MOCK_API_KEY,
      })

      expect(address).toEqual('0xa4a75e55097539ef474613c56d2e70942af42e52')
    })
  })

  describe('Get Install Data', () => {
    test('Module', () => {
      const installData = getInstallData({
        address: MOCK_MODULE_ADDRESS,
        initData: '0xabcd',
        type: MODULE_TYPE_ID_VALIDATOR,
        deInitData: '0x0000',
        additionalContext: '0x0000',
      })

      expect(installData).toEqual(
        '',
      )
    })
  })

  describe('Get Packed Signature', () => {
    test('Mock signature', async () => {
      const hash = '0xabcd'
      const mockSignature = '0x1234'
      const validator = {
        address: '0xe35b75e5ec3c04e9cefa8e581fbee859f56edeb4' as Address,
        isRoot: true,
      }
      const signature = await getPackedSignature(
        async (_) => mockSignature,
        hash,
        validator,
      )

      expect(signature).toEqual(
        '0xe35b75e5ec3c04e9cefa8e581fbee859f56edeb41234',
      )
    })
  })
})
